mapper.select:
SELECT USERID, USERPASS, USERNAME
	FROM SH_MEMBER
WHERE USERID =#{userId}
AND USERPASS =#{userPass}

mapper.update:
UPDATE SH_MEMBER SET
USERPASS =#{userPass},
USERNAME=#{userName}
WHERE USERID =#{userId}

mapper.delete:
DELETE FROM SH_MEMBER
WHERE USERID = #{userId}
    AND USERPASS = #{userPass}

mapper.select:
SELETE COUNT(*) FROM SH_MEMBER
WHERE USERID = #{userId}
	AND UESRPASS =#{userPass}
